import React, {useEffect, useState} from 'react';
import axios from 'axios';

const useUserData = () => {
    const [loading, setLoading] = useState(true);
    const [data, setData] = useState([])


    useEffect(() => {
        const getData = async () => {
            try {
                const {data: response} = await axios.get(`https://api-${process.env.REACT_APP_APPLICATION_ID}.sendbird.com/v3/users`)
                setData(response)
            } catch (e) {
                console.error(e)
            }
            setLoading(false);
        }
        UserData()
    }, [])
    return {
        data,
        loading
    }
}

export default useUserData;
